// reset password
export const resetPassword = async (req, res) => {
  try {
    // get user based on token
    const hashToken = crypto
      .createHash('sha256')
      .update(req.params.tokenback)
      .digest('hex');
    const userFind = await database.getResetToken(hashToken);
    console.log(hashToken);
    console.log(userFind.rows[0].resetToken);
    const found = userFind.rows[0].expires;
    const email = userFind.rows[0].userEmail;
    console.log(email);
    if (found) {
      const expired = Date.parse(found) < new Date();
      if (expired) {
        res.status(403).json({
          status: res.statusCode,
          error: 'Your Token has expired!'
        });
      } else {
        // get a new password
        const { newPass } = req.body;
        await database.deleteResetToken(hashToken);
        const pass = passHash(newPass);
        const updated = await database.updatePassword(email, pass);
        if (updated) {
          res.redirect(
            'https://francois-mugorozi.github.io/Papel/UI/pages/login.html'
          );
          res.status(200).json({
            status: res.statusCode,
            message: 'Password changed successfully'
          });
        } else {
          res.status(500).json({
            status: res.statusCode,
            error: 'Something went wrong, try again later!'
          });
        }
      }
    }
  } catch (err) {
    errorHandle(res, err);
  }
};

// logout user
export const logout = (req, res) => {
  res.cookie('token', 'tokendestroyed', {
    expiresIn: new Date(Date.now() + 24 * 60 * 60 * 1000),
    secure: process.env.NODE_ENV === 'production',
    httpOnly: true
  });
  res.status(200).json({
    status: res.statusCode,
    message: 'User logged out successfully',
    loggedOut: true
  });
};

import generateId from 'uuid/v1';
import passHash from '../helpers/passHash';

class User {
  // initialization
  constructor() {
    this.id = '';
    this.firstName = '';
    this.lastName = '';
    this.email = '';
    this.role = '';
    this.isAdmin = '';
    this.status = '';
    this.password = '';
  }

  // setting up user

  setUser(firstName, lastName, email, role, password) {
    this.id = generateId();
    this.firstName = firstName;
    this.lastName = lastName;
    this.email = email;
    this.role = role;
    this.isAdmin = role === 'admin';
    this.status = 'active';
    this.password = passHash(password);
  }

  // user getter
  getUser() {
    return {
      id: this.id,
      firstName: this.firstName,
      lastName: this.lastName,
      email: this.email,
      role: this.role,
      isAdmin: this.isAdmin,
      status: this.status,
      password: this.password
    };
  }
}

export default User;

